jQuery(function(){
    $(".menu_toggle").click(function(){
        $(".header_menu").stop().slideToggle("fast");
    });

	$(window).on("resize",function(){
		if($(window).width() > 768) {
			$(".header_menu").css("display","flex");
		} else if($(window).width() <= 768) {
			$(".header_menu").css("display","none");
		}
	});
});