mapper.sql:
SELECT * FROM board


	
		sub_cname=#{keyword}
	
	
	
		id=#{keyword}
	
	
	
		title LIKE CONCAT(CONCAT('%', #{keyword}), '%')
	
	
	
		content LIKE CONCAT(CONCAT('%', #{keyword}), '%')

mapper.update:
UPDATE board
SET sub_cname=#{subCname}, title=#{title}, content=#{content}, image=#{image}, reg_date=NOW()
WHERE bid=#{bid}
